import { createClient } from '@supabase/supabase-js'
import { NextResponse } from 'next/server'
import { NEXT_PUBLIC_SUPABASE_URL, SUPABASE_SERVICE_ROLE_KEY } from '@/lib/env_config'

const supabase = createClient(NEXT_PUBLIC_SUPABASE_URL, SUPABASE_SERVICE_ROLE_KEY)

export async function POST(request: Request) {
    try {
        const body = await request.json()
        const { phone_number } = body

        if (!phone_number) {
            return NextResponse.json({ error: 'Phone number is required' }, { status: 400 })
        }

        // Set is_ai_paused = false
        const { data, error } = await supabase
            .from('conversation_states')
            .update({
                is_ai_paused: false,
                resumed_at: new Date().toISOString(),
                updated_at: new Date().toISOString()
            })
            .eq('phone_number', phone_number)
            .select()

        if (error) {
            console.error('Supabase Update Error:', error)
            return NextResponse.json({ error: error.message }, { status: 500 })
        }

        return NextResponse.json({ success: true, message: 'AI Resumed' }, { status: 200 })

    } catch (e) {
        return NextResponse.json({ error: 'Internal Server Error' }, { status: 500 })
    }
}

import { createClient } from '@supabase/supabase-js'
import { NextResponse } from 'next/server'
import { NEXT_PUBLIC_SUPABASE_URL, SUPABASE_SERVICE_ROLE_KEY } from '@/lib/env_config'

// Initialize Supabase Client with Service Role Key for full access (RLS Bypass)
// Initialize outside but validation happens inside or we move it inside to be safe against runtime env issues
// const supabase = createClient(NEXT_PUBLIC_SUPABASE_URL, SUPABASE_SERVICE_ROLE_KEY)

export async function POST(request: Request) {
    let supabase;
    try {
        if (!NEXT_PUBLIC_SUPABASE_URL || !SUPABASE_SERVICE_ROLE_KEY) {
            throw new Error("Missing Supabase Environment Variables")
        }
        supabase = createClient(NEXT_PUBLIC_SUPABASE_URL, SUPABASE_SERVICE_ROLE_KEY)
    } catch (err: any) {
        return NextResponse.json({ error: 'Server Configuration Error' }, { status: 500 })
    }

    try {
        const body = await request.json()
        console.log("INCOMING BODY:", body)
        const { phone_number, message, session_id } = body

        if (!message || !phone_number) {
            return NextResponse.json({ error: 'Missing required fields' }, { status: 400 })
        }

        // 1. Get Institute ID from Leads
        const { data: leadData, error: leadError } = await supabase
            .from('leads')
            .select('institute_id')
            .eq('phone', phone_number)
            .single()

        if (leadError || !leadData?.institute_id) {
            // Fallback: Check if we can find any lead with this phone, maybe not 'active'? 
            // For now, strict check.
            return NextResponse.json({ error: 'Lead not found or missing Institute ID' }, { status: 404 })
        }

        const instituteId = leadData.institute_id

        // 2. Get WhatsApp Credentials
        const { data: instData, error: instError } = await supabase
            .from('institutes')
            .select('phone_id, access_token')
            .eq('id', instituteId)
            .single()

        if (instError || !instData?.phone_id || !instData?.access_token) {
            return NextResponse.json({ error: 'WhatsApp Credentials not found' }, { status: 403 })
        }

        const { phone_id, access_token } = instData

        // 3. Send Message via Meta API
        const metaUrl = `https://graph.facebook.com/v17.0/${phone_id}/messages`
        const payload = {
            messaging_product: "whatsapp",
            to: phone_number,
            type: "text",
            text: { body: message }
        }

        const metaResponse = await fetch(metaUrl, {
            method: 'POST',
            headers: {
                'Authorization': `Bearer ${access_token}`,
                'Content-Type': 'application/json'
            },
            body: JSON.stringify(payload)
        })

        const metaRaw = await metaResponse.text()
        console.log("META STATUS:", metaResponse.status)
        console.log("RAW META RESPONSE:", metaRaw)

        let metaResult;
        try {
            metaResult = JSON.parse(metaRaw)
        } catch (e) {
            metaResult = { error: "Failed to parse JSON", raw: metaRaw }
        }

        if (!metaResponse.ok) {
            return NextResponse.json({ error: 'Meta API Failed', details: metaResult }, { status: 500 })
        }

        // 4. Update Conversation State
        await supabase
            .from('conversation_states')
            .upsert({
                phone_number: phone_number,
                institute_id: instituteId,
                is_ai_paused: true,
                paused_at: new Date().toISOString(),
                updated_at: new Date().toISOString()
            }, { onConflict: 'phone_number' })


        // 5. Log to Chat History
        // Ensure session_id is a valid UUID, otherwise generate one
        const isValidUUID = (id: string) => /^[0-9a-f]{8}-[0-9a-f]{4}-[0-9a-f]{4}-[0-9a-f]{4}-[0-9a-f]{12}$/i.test(id)
        const finalSessionId = (session_id && isValidUUID(session_id)) ? session_id : crypto.randomUUID()

        const metaId = metaResult.messages?.[0]?.id

        const { data: chatData, error: chatError } = await supabase
            .from('ai_chat_history')
            .insert([
                {
                    phone_number: phone_number,
                    session_id: finalSessionId,
                    institute_id: instituteId, // Added institute_id which is likely required
                    ai_response: message,
                    user_message: "", // REQUIRED field (cannot be null)
                    role: 'assistant',
                    intent: 'Manual Override',
                    sentiment: 'Neutral',
                    status: 'sent',
                    whatsapp_message_id: metaId,
                    // Storing extra metadata in JSONB column 'message_meta' instead of non-existent columns
                    message_meta: {
                        whatsapp_message_id: metaId,
                        status: 'sent',
                        source: 'dashboard_manual'
                    }
                }
            ])
            .select()

        if (chatError) {
            // Log error internally but don't expose details to client
            console.error("Chat History Insert Error:", chatError)
        }

        return NextResponse.json({
            success: true,
            meta_id: metaResult.messages?.[0]?.id,
            data: chatData
        }, { status: 200 })

    } catch (e: any) {
        console.error("Chat Send Error:", e)
        return NextResponse.json({ error: 'Internal Server Error' }, { status: 500 })
    }
}

import { createClient } from '@supabase/supabase-js'
import { NextResponse } from 'next/server'
import { NEXT_PUBLIC_SUPABASE_URL, SUPABASE_SERVICE_ROLE_KEY } from '@/lib/env_config'

// Use Service Role for DB Updates (Bypass RLS)
const supabase = createClient(NEXT_PUBLIC_SUPABASE_URL, SUPABASE_SERVICE_ROLE_KEY)

// 1. GET Request - For Webhook Verification
export async function GET(request: Request) {
    const { searchParams } = new URL(request.url)
    const mode = searchParams.get('hub.mode')
    const token = searchParams.get('hub.verify_token')
    const challenge = searchParams.get('hub.challenge')

    // You should define a verify token in your env, e.g. "adwelink_verify_123"
    // For now, accepting "adwelink" or any strict match
    if (mode === 'subscribe' && token === 'adwelink') {
        console.log("Webhook Verified")
        return new NextResponse(challenge, { status: 200 })
    }

    return NextResponse.json({ error: 'Forbidden' }, { status: 403 })
}

// 2. POST Request - Event Handling
export async function POST(request: Request) {
    try {
        const body = await request.json()
        const entry = body.entry?.[0]
        const changes = entry?.changes?.[0]
        const value = changes?.value

        if (!value) return NextResponse.json({ message: 'No value' }, { status: 200 })

        // A. Handle Status Updates (Sent, Delivered, Read)
        if (value.statuses && value.statuses.length > 0) {
            const statusUpdate = value.statuses[0]
            const metaId = statusUpdate.id
            const newStatus = statusUpdate.status // 'sent', 'delivered', 'read', 'failed'
            const timestamp = statusUpdate.timestamp

            console.log(`Status Update: ${newStatus} for ID: ${metaId}`)

            // Update DB (Simple update, assumes columns added via SQL Editor)
            let { error } = await supabase
                .from('ai_chat_history')
                .update({ status: newStatus })
                .eq('whatsapp_message_id', metaId)

            if (error) {
                console.error("Webhook DB Update Error (by whatsapp_message_id):", error)
                // Fallback: Try updating by sessionId if whatsapp_message_id is not yet indexed or available
                // This assumes 'from' in the message object corresponds to a sessionId in ai_chat_history
                const sessionId = value.messages?.[0]?.from || value.contacts?.[0]?.wa_id;
                if (sessionId) {
                    console.log(`Attempting fallback update for sessionId: ${sessionId}`);
                    ({ error } = await supabase
                        .from('ai_chat_history')
                        .update({ status: newStatus })
                        .eq('session_id', sessionId)
                        .order('created_at', { ascending: false }) // Update the most recent message for this session
                        .limit(1)
                        .single()); // Ensure only one record is updated
                    if (error) {
                        console.error("Webhook DB Update Error (by sessionId fallback):", error);
                    }
                }
            }
        }

        // B. Handle Incoming Messages (Optional: If not handled by n8n)
        // User said n8n is used, so we might skip this or just log it. 
        // We focus on STATUS for now as requested.

        return NextResponse.json({ success: true }, { status: 200 })

    } catch (e) {
        console.error("Webhook Error", e)
        return NextResponse.json({ error: 'Internal Server Error' }, { status: 500 })
    }
}

"use client"
import { useState } from "react"
import { createClient } from "@/lib/supabase"
import { useRouter, useSearchParams } from "next/navigation"
import { Button } from "@/components/ui/button"
import { Input } from "@/components/ui/input"
import { Card, CardHeader, CardContent, CardTitle, CardDescription, CardFooter } from "@/components/ui/card"
import { Label } from "@/components/ui/label"
import { Loader2, Sparkles } from "lucide-react"
import { useEffect } from "react"
import Image from "next/image"

export default function LoginPage() {
    const router = useRouter()
    const searchParams = useSearchParams()
    const [email, setEmail] = useState("")
    const [password, setPassword] = useState("")
    const [loading, setLoading] = useState(false)
    const [error, setError] = useState<string | null>(null)

    // Handle Demo Mode
    useEffect(() => {
        const demo = searchParams.get("demo")
        if (demo === "sharma") {
            setEmail("sharma.demo@gmail.com")
            setPassword("sharma123")
        }
    }, [searchParams])

    const handleLogin = async (e: React.FormEvent) => {
        e.preventDefault()
        setLoading(true)
        setError(null)

        const supabase = createClient()
        const { error } = await supabase.auth.signInWithPassword({
            email,
            password,
        })

        if (error) {
            setError(error.message)
            setLoading(false)
        } else {
            router.push("/home")
            router.refresh()
        }
    }

    return (
        <div className="h-[calc(100vh-40px)] w-full overflow-hidden flex items-center justify-center bg-[#0B0F19] p-4 text-white">
            {/* Background Glow */}
            <div className="absolute top-1/2 left-1/2 -translate-x-1/2 -translate-y-1/2 h-[500px] w-[500px] bg-purple-600/20 blur-[120px] rounded-full pointer-events-none" />

            <Card className="w-full max-w-md bg-white/5 border-white/10 backdrop-blur-md relative z-10">
                <CardHeader className="space-y-4 text-center">
                    <div className="relative h-12 w-48 mx-auto">
                        <Image
                            src="/branding/adwelink.svg"
                            alt="Adwelink"
                            fill
                            className="object-contain"
                            priority
                        />
                    </div>
                    <CardDescription className="text-slate-400">
                        Agent Management System
                    </CardDescription>

                    {searchParams.get("demo") === "sharma" && (
                        <div className="mt-4 inline-flex items-center gap-2 px-3 py-1 rounded-full bg-amber-500/10 border border-amber-500/20 text-[10px] text-amber-500 font-bold uppercase tracking-widest animate-pulse">
                            <Sparkles className="h-3 w-3" /> Access Granted: Sharma Classes Demo
                        </div>
                    )}
                </CardHeader>
                <CardContent>
                    <form onSubmit={handleLogin} className="space-y-4">
                        <div className="space-y-2">
                            <Label htmlFor="email">Email</Label>
                            <Input
                                id="email"
                                type="email"
                                placeholder="admin@adwelink.com"
                                className="bg-black/40 border-white/10 text-white placeholder:text-slate-600 focus:border-purple-500/50"
                                value={email}
                                onChange={(e) => setEmail(e.target.value)}
                                required
                            />
                        </div>
                        <div className="space-y-2">
                            <Label htmlFor="password">Password</Label>
                            <Input
                                id="password"
                                type="password"
                                className="bg-black/40 border-white/10 text-white focus:border-purple-500/50"
                                value={password}
                                onChange={(e) => setPassword(e.target.value)}
                                required
                            />
                        </div>
                        {error && (
                            <div className="text-xs text-red-400 bg-red-500/10 p-2 rounded border border-red-500/20">
                                {error}
                            </div>
                        )}
                        <Button
                            type="submit"
                            className="w-full bg-purple-600 hover:bg-purple-700 text-white font-semibold shadow-lg shadow-purple-900/20"
                            disabled={loading}
                        >
                            {loading ? <Loader2 className="mr-2 h-4 w-4 animate-spin" /> : "Sign In"}
                        </Button>
                    </form>
                </CardContent>
                <CardFooter className="flex justify-center border-t border-white/5 pt-4">
                    <p className="text-xs text-slate-500">
                        Authorized Personnel Only. <span className="text-purple-400 hover:underline cursor-pointer">Forgot Password?</span>
                    </p>
                </CardFooter>
            </Card>
        </div>
    )
}

"use server"

import { createServerClient } from "@/lib/supabase-server"
import { revalidatePath } from "next/cache"
import { getAuthenticatedInstituteId } from "@/lib/auth-utils"

export async function getInstituteSettings() {
    const supabase = await createServerClient()
    const instituteId = await getAuthenticatedInstituteId(supabase)

    const { data, error } = await supabase
        .from("institutes")
        .select("*")
        .eq("id", instituteId)
        .single()

    if (error) throw new Error(error.message)
    return data
}

export async function updateInstituteSettings(data: any) {
    const supabase = await createServerClient()
    const instituteId = await getAuthenticatedInstituteId(supabase)

    const { error } = await supabase
        .from("institutes")
        .update(data)
        .eq("id", instituteId)

    if (error) throw new Error(error.message)

    revalidatePath("/settings")
    return { success: true }
}

"use server"

import { createServerClient } from "@/lib/supabase-server"
import { revalidatePath } from "next/cache"
import { getAuthenticatedInstituteId } from "@/lib/auth-utils"

export async function getCourses() {
    const supabase = await createServerClient()
    const institute_id = await getAuthenticatedInstituteId(supabase)

    const { data, error } = await supabase
        .from("courses")
        .select("*")
        .eq("institute_id", institute_id)
        .order("name", { ascending: true })

    if (error) {
        throw new Error(error.message)
    }

    return data
}

export async function createCourse(data: any) {
    const supabase = await createServerClient()

    // Resilient institute identification
    const institute_id = await getAuthenticatedInstituteId(supabase)

    const { data: course, error } = await supabase
        .from("courses")
        .insert({
            ...data,
            institute_id
        })
        .select()
        .single()

    if (error) {
        console.error("Create course error:", error)
        throw new Error(error.message)
    }

    revalidatePath("/workspace/courses")
    return { success: true, data: course }
}

export async function updateCourse(courseId: string, data: any) {
    const supabase = await createServerClient()
    const institute_id = await getAuthenticatedInstituteId(supabase)

    const { error } = await supabase
        .from("courses")
        .update(data)
        .eq("id", courseId)
        .eq("institute_id", institute_id)

    if (error) {
        throw new Error(error.message)
    }

    revalidatePath("/workspace/courses")
    return { success: true }
}

export async function deleteCourse(courseId: string) {
    const supabase = await createServerClient()
    const institute_id = await getAuthenticatedInstituteId(supabase)

    const { error } = await supabase
        .from("courses")
        .delete()
        .eq("id", courseId)
        .eq("institute_id", institute_id)

    if (error) {
        throw new Error(error.message)
    }

    revalidatePath("/workspace/courses")
    return { success: true }
}

"use client"

import * as React from "react"
import { createClient } from "@/lib/supabase"
import { Database } from "@/lib/database.types"
import { Badge } from "@/components/ui/badge"
import { Button } from "@/components/ui/button"
import {
    Card,
    CardContent,
    CardDescription,
    CardHeader,
    CardTitle,
} from "@/components/ui/card"
import {
    Dialog,
    DialogContent,
    DialogDescription,
    DialogHeader,
    DialogTitle,
    DialogTrigger,
} from "@/components/ui/dialog"
import { Input } from "@/components/ui/input"
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select"
import {
    BookOpen,
    Plus,
    Clock,
    Banknote,
    Sparkles,
    MonitorPlay,
    Building2,
    Users2,
    ArrowRight,
    Search,
    GraduationCap,
    Trophy,
    Target,
    Calendar,
    Hash,
    ChevronRight,
    UserCircle2,
    Edit3,
    Trash2,
    Save,
    X,
    LayoutGrid,
    Layers
} from "lucide-react"
import { getCourses, createCourse, updateCourse, deleteCourse } from "./actions"

type Course = Database["public"]["Tables"]["courses"]["Row"]

export default function CoursesPage() {
    const [courses, setCourses] = React.useState<Course[]>([])
    const [loading, setLoading] = React.useState(true)
    const [selectedCourse, setSelectedCourse] = React.useState<Course | null>(null)
    const [dialogOpen, setDialogOpen] = React.useState(false)
    const [isEditing, setIsEditing] = React.useState(false)
    const [formData, setFormData] = React.useState<Partial<Course>>({})
    const [searchQuery, setSearchQuery] = React.useState("")
    const [deletingIds, setDeletingIds] = React.useState<Set<string>>(new Set())

    const fetchData = async () => {
        setLoading(true)
        try {
            const data = await getCourses()
            setCourses(data || [])
            // end-mock-injection
        } catch (error) {
            console.error("Failed to fetch courses:", error)
        } finally {
            setLoading(false)
        }
    }

    React.useEffect(() => {
        fetchData()
    }, [])

    const handleNewCourse = () => {
        setSelectedCourse(null)
        setFormData({
            name: "",
            total_fee: 0,
            duration_months: 12,
            mode: "Offline", // DB expects Capitalized 'Offline'
            registration_fee: 0,
            target_class: "",
        })
        setIsEditing(false)
        setDialogOpen(true)
    }

    const handleEditCourse = (course: Course) => {
        setSelectedCourse(course)
        setFormData(course)
        setIsEditing(true)
        setDialogOpen(true)
    }

    const handleDeleteCourse = async (courseId: string) => {
        if (!confirm("Are you sure you want to delete this course? This action cannot be undone.")) return

        setDeletingIds(prev => new Set(prev).add(courseId))
        try {
            await deleteCourse(courseId)
            setCourses(courses.filter(c => c.id !== courseId))
        } catch (error: any) {
            console.error("Failed to delete course:", error)
            // Show a more helpful error message
            if (error.message?.includes("fk") || error.message?.includes("violates foreign key constraint")) {
                alert("Cannot delete this course because it is linked to active batches. Please delete or move the batches first.")
            } else {
                alert("Failed to delete course: " + (error.message || "Unknown error"))
            }
        } finally {
            setDeletingIds(prev => {
                const next = new Set(prev)
                next.delete(courseId)
                return next
            })
        }
    }

    const handleSave = async () => {
        try {
            const { id, created_at, updated_at, institute_id, ...payload } = formData

            if (isEditing && selectedCourse) {
                await updateCourse(selectedCourse.id, payload)
                setCourses(courses.map(c => c.id === selectedCourse.id ? { ...c, ...payload } as Course : c))
            } else {
                const response = await createCourse(payload)
                if (response.success && response.data) {
                    setCourses([...courses, response.data])
                }
            }
            setDialogOpen(false)
        } catch (error) {
            console.error("Failed to save course:", error)
        }
    }

    const filteredCourses = courses.filter(c =>
        c.name.toLowerCase().includes(searchQuery.toLowerCase()) ||
        (c.target_class?.toLowerCase() || "").includes(searchQuery.toLowerCase())
    )

    return (
        <div className="h-[calc(100vh-40px)] w-full overflow-hidden flex flex-col">
            {/* Main Scrollable Container */}
            <div className="flex-1 w-full h-full overflow-y-auto custom-scrollbar relative">

                {/* Header Section (Transparent & Static) */}
                <div className="px-4 md:px-8 py-6 mb-6">
                    <div className="relative z-10 flex flex-col md:flex-row items-start md:items-center justify-between gap-4 max-w-7xl mx-auto">
                        <div className="space-y-1">
                            <h2 className="text-2xl md:text-3xl font-bold tracking-tight text-white flex items-center gap-3">
                                <BookOpen className="h-8 w-8 text-emerald-500" />
                                Courses & Fees
                                <Badge variant="outline" className="bg-primary/10 text-primary border-primary/20 text-[10px] py-0 px-2 uppercase tracking-widest hidden sm:inline-flex">
                                    {courses.length} Active
                                </Badge>
                            </h2>
                            <p className="text-muted-foreground text-sm md:text-base">Manage your batch details, student categories, and fee structures.</p>
                        </div>

                        <div className="flex items-center gap-3 w-full md:w-auto">
                            <Button
                                onClick={handleNewCourse}
                                className="flex-1 md:flex-none bg-primary hover:bg-primary/90 text-primary-foreground shadow-lg shadow-primary/25 h-10 px-5 font-bold rounded-xl"
                            >
                                <Plus className="mr-2 h-4 w-4 stroke-[3px]" /> Add New Course
                            </Button>
                        </div>
                    </div>

                    <div className="relative z-10 flex flex-col sm:flex-row gap-4 mt-6 max-w-7xl mx-auto">
                        <div className="relative flex-1 group">
                            <Search className="absolute left-3 top-1/2 -translate-y-1/2 h-4 w-4 text-slate-500 group-focus-within:text-white transition-colors" />
                            <Input
                                placeholder="Search by name, batch or standard..."
                                value={searchQuery}
                                onChange={(e) => setSearchQuery(e.target.value)}
                                className="bg-black/20 border-white/10 pl-10 h-10 rounded-xl text-white text-sm focus:bg-black/40 transition-colors"
                            />
                        </div>
                    </div>
                </div>

                {/* Data Cards (Scrollable Content) */}
                <div className="pb-20 px-4 md:px-8 max-w-7xl mx-auto">
                    <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-5">
                        {loading ? (
                            [1, 2, 3].map(i => (
                                <div key={i} className="h-56 rounded-2xl bg-white/5 border border-white/10 animate-pulse" />
                            ))
                        ) : filteredCourses.length === 0 ? (
                            <div className="col-span-full py-24 text-center rounded-2xl border border-dashed border-white/10">
                                <LayoutGrid className="h-12 w-12 text-slate-700 mx-auto mb-4" />
                                <h3 className="text-xl font-bold text-white mb-1">No Courses Yet</h3>
                                <p className="text-slate-500 text-sm">Create your first course to begin enrollment management.</p>
                            </div>
                        ) : (
                            filteredCourses.map((course) => (
                                <Card
                                    key={course.id}
                                    className="bg-gradient-to-br from-violet-500/10 to-transparent border-white/10 border-violet-500/20 backdrop-blur-md shadow-xl hover:border-primary/50 hover:scale-[1.02] transition-all group relative overflow-hidden rounded-2xl"
                                >
                                    <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-3">
                                        <CardTitle className="text-[10px] font-bold uppercase tracking-widest text-slate-500 flex items-center gap-1.5 leading-none">
                                            <Hash className="h-3 w-3" /> {course.id.slice(0, 8)}
                                        </CardTitle>
                                        <div className="flex items-center gap-1.5 opacity-60 group-hover:opacity-100 transition-opacity">
                                            <Button
                                                variant="ghost"
                                                size="sm"
                                                disabled={deletingIds.has(course.id)}
                                                onClick={() => handleEditCourse(course)}
                                                className="h-7 w-7 p-0 text-slate-400 hover:text-white hover:bg-white/10 rounded-lg"
                                                title="Edit"
                                            >
                                                <Edit3 className="h-3.5 w-3.5" />
                                            </Button>
                                            <Button
                                                variant="ghost"
                                                size="sm"
                                                disabled={deletingIds.has(course.id)}
                                                onClick={() => handleDeleteCourse(course.id)}
                                                className={`h-7 w-7 p-0 rounded-lg transition-colors ${deletingIds.has(course.id) ? 'bg-red-500/20 text-red-400 animate-pulse' : 'text-slate-600 hover:text-red-400 hover:bg-red-500/10'}`}
                                                title="Delete"
                                            >
                                                {deletingIds.has(course.id) ? (
                                                    <Clock className="h-3.5 w-3.5 animate-spin" />
                                                ) : (
                                                    <Trash2 className="h-3.5 w-3.5" />
                                                )}
                                            </Button>
                                        </div>
                                    </CardHeader>

                                    <CardContent className="pt-0">
                                        <div className="space-y-4">
                                            <div className="space-y-1.5">
                                                <div className="text-lg font-bold text-white leading-tight">
                                                    {course.name}
                                                </div>
                                                <div className="flex items-center gap-2">
                                                    <Badge variant="outline" className="bg-primary/5 text-[9px] text-primary border-primary/20 uppercase py-0 px-2 font-black tracking-tight rounded-md">
                                                        {course.mode || "Offline"}
                                                    </Badge>
                                                    <div className="flex items-center gap-1 text-[10px] text-slate-400 font-bold uppercase tracking-tight">
                                                        <Layers className="h-3 w-3 text-sky-400" /> Batch: <span className="text-white">{course.target_class || "All Students"}</span>
                                                    </div>
                                                </div>
                                            </div>

                                            <div className="grid grid-cols-2 gap-4 pt-4 border-t border-white/5">
                                                <div className="space-y-1">
                                                    <div className="text-[9px] text-slate-500 uppercase font-bold tracking-widest">Total Fee</div>
                                                    <div className="text-xl font-bold text-white font-mono leading-none flex items-baseline gap-1">
                                                        <span className="text-xs font-bold text-slate-500">â‚¹</span>
                                                        {Number(course.total_fee).toLocaleString()}
                                                    </div>
                                                </div>
                                                <div className="space-y-1 text-right">
                                                    <div className="text-[9px] text-slate-500 uppercase font-bold tracking-widest">Duration</div>
                                                    <div className="text-sm font-bold text-slate-300 flex items-center justify-end gap-1.5">
                                                        <Clock className="h-3.5 w-3.5 text-slate-600" /> {course.duration_months} Months
                                                    </div>
                                                </div>
                                            </div>

                                            <div className="flex items-center justify-between pt-4 border-t border-white/5 opacity-60">
                                                <div className="flex items-center gap-1.5 text-[9px] text-slate-500 font-bold uppercase tracking-widest">
                                                    <Calendar className="h-3 w-3" />
                                                    {course.created_at ? new Date(course.created_at).toLocaleDateString() : 'N/A'}
                                                </div>
                                                <div className="text-[9px] text-slate-500 uppercase font-bold tracking-widest">
                                                    Adm. Fee: <span className="text-white">â‚¹{Number(course.registration_fee || 0).toLocaleString()}</span>
                                                </div>
                                            </div>
                                        </div>
                                    </CardContent>
                                </Card>
                            ))
                        )}
                    </div>
                </div>

                {/* Add/Edit Dialog */}
                <Dialog open={dialogOpen} onOpenChange={setDialogOpen}>
                    <DialogContent className="bg-[#0B0F19] border-white/10 text-white max-w-lg p-0 overflow-hidden outline-none shadow-[0_0_100px_rgba(0,0,0,0.8)] rounded-3xl">
                        <DialogHeader className="p-8 bg-gradient-to-b from-white/5 to-transparent border-b border-white/5">
                            <div className="flex items-center justify-between">
                                <div className="flex items-center gap-3">
                                    <div className={`h-10 w-10 rounded-xl flex items-center justify-center border ${isEditing ? 'bg-primary/10 border-primary/20 text-primary' : 'bg-sky-500/10 border-sky-500/20 text-sky-400'}`}>
                                        {isEditing ? <Edit3 className="h-5 w-5" /> : <Plus className="h-5 w-5" />}
                                    </div>
                                    <div className="space-y-0.5">
                                        <DialogTitle className="text-xl font-bold tracking-tight">
                                            {isEditing ? "Update Course" : "New Course Details"}
                                        </DialogTitle>
                                        <DialogDescription className="text-slate-500 text-xs">
                                            {isEditing ? "Modify the course name, batch, or fee mapping." : "Enter the primary details for your new academic course."}
                                        </DialogDescription>
                                    </div>
                                </div>
                            </div>
                        </DialogHeader>

                        <div className="p-8 space-y-6">
                            {/* Identity Controls */}
                            <div className="space-y-4">
                                <div className="space-y-2">
                                    <label className="text-[10px] font-bold uppercase tracking-[0.2em] text-slate-600 ml-1">Full Course Name</label>
                                    <Input
                                        placeholder="e.g. NEET Crash Course"
                                        value={formData.name || ""}
                                        onChange={(e) => setFormData({ ...formData, name: e.target.value })}
                                        className="bg-white/[0.03] border-white/10 text-white h-12 focus:ring-1 focus:ring-primary/40 text-sm font-bold rounded-xl"
                                    />
                                </div>

                                <div className="grid grid-cols-2 gap-5">
                                    <div className="space-y-2">
                                        <label className="text-[10px] font-bold uppercase tracking-[0.2em] text-slate-600 ml-1">Target Batch / Class</label>
                                        <Input
                                            placeholder="e.g. 10th, 12th, or Droppers"
                                            value={formData.target_class || ""}
                                            onChange={(e) => setFormData({ ...formData, target_class: e.target.value })}
                                            className="bg-white/[0.03] border-white/10 text-white h-12 text-sm font-medium rounded-xl"
                                        />
                                    </div>
                                    <div className="space-y-2">
                                        <label className="text-[10px] font-bold uppercase tracking-[0.2em] text-slate-600 ml-1">Study Mode</label>
                                        <Select
                                            value={formData.mode || "Offline"}
                                            onValueChange={(val) => setFormData({ ...formData, mode: val })}
                                        >
                                            <SelectTrigger className="bg-white/[0.03] border-white/10 text-white h-12 text-sm font-bold rounded-xl capitalize">
                                                <SelectValue />
                                            </SelectTrigger>
                                            <SelectContent className="bg-[#161B22] border-white/10 text-white rounded-xl">
                                                <SelectItem value="Offline">Offline</SelectItem>
                                                <SelectItem value="Online">Online</SelectItem>
                                                <SelectItem value="Hybrid">Hybrid</SelectItem>
                                            </SelectContent>
                                        </Select>
                                    </div>
                                </div>
                            </div>

                            {/* Financials & Duration */}
                            <div className="pt-6 border-t border-white/5">
                                <h4 className="text-[10px] font-bold text-primary uppercase tracking-[0.3em] mb-5">Fees & Program Cycle</h4>
                                <div className="grid grid-cols-3 gap-5">
                                    <div className="space-y-2">
                                        <label className="text-[9px] font-bold uppercase text-slate-600">Total Fee</label>
                                        <div className="relative">
                                            <span className="absolute left-3 top-1/2 -translate-y-1/2 text-slate-600 font-mono text-xs">â‚¹</span>
                                            <Input
                                                type="number"
                                                value={formData.total_fee || ""}
                                                onChange={(e) => setFormData({ ...formData, total_fee: parseFloat(e.target.value) })}
                                                className="bg-primary/5 border-primary/20 text-primary pl-7 h-11 font-mono font-black text-sm rounded-xl"
                                            />
                                        </div>
                                    </div>
                                    <div className="space-y-2">
                                        <label className="text-[9px] font-bold uppercase text-slate-600">Admission Fee</label>
                                        <div className="relative">
                                            <span className="absolute left-3 top-1/2 -translate-y-1/2 text-slate-600 font-mono text-xs">â‚¹</span>
                                            <Input
                                                type="number"
                                                value={formData.registration_fee || ""}
                                                onChange={(e) => setFormData({ ...formData, registration_fee: parseFloat(e.target.value) })}
                                                className="bg-white/[0.03] border-white/10 text-white pl-7 h-11 font-mono font-bold text-sm rounded-xl"
                                            />
                                        </div>
                                    </div>
                                    <div className="space-y-2">
                                        <label className="text-[9px] font-bold uppercase text-slate-600">Cycle (m)</label>
                                        <Input
                                            type="number"
                                            value={formData.duration_months || ""}
                                            onChange={(e) => setFormData({ ...formData, duration_months: parseInt(e.target.value) })}
                                            className="bg-white/[0.03] border-white/10 text-white h-11 font-mono font-bold text-sm rounded-xl"
                                        />
                                    </div>
                                </div>
                            </div>
                        </div>

                        <div className="p-8 bg-white/5 border-t border-white/5 flex gap-4">
                            <Button
                                onClick={handleSave}
                                className="flex-1 bg-primary hover:bg-primary/90 text-primary-foreground h-12 font-bold rounded-2xl shadow-xl shadow-primary/20 transition-all active:scale-[0.98] flex items-center justify-center gap-2"
                            >
                                <Save className="h-4 w-4" /> Save Changes
                            </Button>
                            <Button
                                variant="outline"
                                onClick={() => setDialogOpen(false)}
                                className="flex-1 border-white/10 bg-white/5 text-slate-400 hover:text-white h-12 rounded-2xl font-bold flex items-center justify-center gap-2"
                            >
                                <X className="h-4 w-4" /> Cancel
                            </Button>
                        </div>
                    </DialogContent>
                </Dialog>
            </div>
        </div>
    )
}

"use client"

import * as React from "react"
import { Avatar, AvatarFallback, AvatarImage } from "@/components/ui/avatar"
import { Button } from "@/components/ui/button"


import { MessageSquare, Hand, Send, Check, CheckCheck, Users, Sparkles, Search, Smile, ThumbsDown, ChevronDown, Plus, Image as ImageIcon } from "lucide-react"
import { createClient } from "@/lib/supabase"

// Define the shape of our "Flat" DB row
type ChatRow = {
    id: string
    created_at: string | null
    phone_number: string | null
    session_id: string | null
    user_message: string | null
    ai_response: string | null
    sentiment: string | null
    intent: string | null
    status?: 'sent' | 'delivered' | 'read' | 'failed' | null
    message_meta?: Record<string, unknown> | null
    is_read?: boolean
}

// Define the shape of a "Visual" message Bubble
type VisualMessage = {
    id: string
    role: "user" | "assistant"
    content: string
    timestamp: string
    sentiment?: string
    isManual?: boolean
    status?: 'sent' | 'delivered' | 'read' | 'failed' | null
    mediaUrl?: string
}



// Helper to format date separators like WhatsApp
const formatDateDivider = (dateString: string) => {
    const date = new Date(dateString);
    const today = new Date();
    const yesterday = new Date();
    yesterday.setDate(today.getDate() - 1);

    if (date.toDateString() === today.toDateString()) return "Today";
    if (date.toDateString() === yesterday.toDateString()) return "Yesterday";

    return date.toLocaleDateString([], {
        day: 'numeric',
        month: 'long',
        year: today.getFullYear() !== date.getFullYear() ? 'numeric' : undefined
    });
};

export default function FeedPage() {
    const [messages, setMessages] = React.useState<ChatRow[]>([])
    const [sessions, setSessions] = React.useState<string[]>([])
    const [selectedSession, setSelectedSession] = React.useState<string | null>(null)
    const [loading, setLoading] = React.useState(true)
    const [inputText, setInputText] = React.useState("")
    const [isSending, setIsSending] = React.useState(false)
    const [isAiPaused, setIsAiPaused] = React.useState(false)
    const [sessionSearch, setSessionSearch] = React.useState("")
    const scrollBottomRef = React.useRef<HTMLDivElement>(null)
    const scrollContainerRef = React.useRef<HTMLDivElement>(null)
    const [showScrollButton, setShowScrollButton] = React.useState(false)


    const [userMap, setUserMap] = React.useState<Record<string, string>>({})

    // Fetch Chat History & Status
    const fetchHistory = React.useCallback(async () => {
        const supabase = createClient()

        // 1. Get Messages
        const { data: msgData, error: msgError } = await supabase
            .from("ai_chat_history")
            .select("*")
            .order("created_at", { ascending: true })

        if (msgError) {
            console.error("Chat Fetch Error Detailed:", JSON.stringify(msgError, null, 2))
        } else {
            setMessages(msgData || [])

            // 1. Group by session/phone and find latest activity time
            const sessionMap: Record<string, string> = {}
            msgData?.forEach((m: ChatRow) => {
                const sid = m.phone_number || m.session_id || "Unknown"
                const mTime = m.created_at || "0"
                if (!sessionMap[sid] || mTime > sessionMap[sid]) {
                    sessionMap[sid] = mTime
                }
            })

            // 2. Sort sessions by latest activity
            const sortedIds = Object.keys(sessionMap).sort((a, b) => {
                return sessionMap[b].localeCompare(sessionMap[a])
            })

            setSessions(sortedIds)

            // 1.5 Fetch User Names if we have IDs
            if (sortedIds.length > 0) {
                const { data: leadData } = await supabase
                    .from("leads")
                    .select("name, phone")
                    .in("phone", sortedIds)

                if (leadData) {
                    const newMap: Record<string, string> = {}
                    leadData.forEach((l: { phone: string | null; name: string | null }) => {
                        if (l.phone && l.name) newMap[l.phone] = l.name
                    })
                    setUserMap(prev => ({ ...prev, ...newMap }))
                }
            }

            if (sortedIds.length > 0 && !selectedSession) {
                setSelectedSession(sortedIds[0])
            }
        }

        // 2. Get AI Status (if session selected)
        if (selectedSession) {
            const { data: statusData } = await supabase
                .from("conversation_states")
                .select("is_ai_paused")
                .eq("phone_number", selectedSession)
                .single()

            setIsAiPaused(statusData?.is_ai_paused || false)
        }

        setLoading(false)
    }, [selectedSession])

    // Resume AI Handler
    const handleResume = async () => {
        if (!selectedSession) return;
        try {
            await fetch('/api/chat/resume', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({ phone_number: selectedSession })
            })
            setIsAiPaused(false) // Optimistic Update
            fetchHistory()
        } catch (e) {
            console.error("Resume Failed", e)
        }
    }

    // Mark Messages as Read
    const markAsRead = React.useCallback(async (sessionId: string) => {
        const supabase = createClient()

        // Smarter Filter: Don't compare phone numbers to UUID columns (prevents 22P02 error)
        const isUuid = /^[0-9a-f]{8}-[0-9a-f]{4}-[0-5][0-9a-f]{3}-[089ab][0-9a-f]{3}-[0-9a-f]{12}$/i.test(sessionId);

        let query = supabase
            .from("ai_chat_history")
            .update({
                is_read: true,
                updated_at: new Date().toISOString()
            } as any) // eslint-disable-line @typescript-eslint/no-explicit-any
            .eq("is_read", false);

        if (isUuid) {
            query = query.or(`phone_number.eq.${sessionId},session_id.eq.${sessionId}`);
        } else {
            query = query.eq("phone_number", sessionId);
        }

        const { error } = await query;

        if (!error) {
            fetchHistory()
        } else {
            console.error("MarkAsRead Error:", {
                message: error.message,
                details: error.details,
                hint: error.hint,
                code: error.code
            })
        }
    }, [fetchHistory])

    React.useEffect(() => {
        fetchHistory()
        // Poll for updates every 5 seconds (Simple Real-time)
        const interval = setInterval(fetchHistory, 5000)
        return () => clearInterval(interval)
    }, [fetchHistory])

    // When session changes, mark as read
    React.useEffect(() => {
        if (selectedSession) {
            markAsRead(selectedSession)
        }
    }, [selectedSession, markAsRead])

    const scrollToBottom = () => {
        scrollBottomRef.current?.scrollIntoView({ behavior: "smooth" })
    }

    // Smart Auto-Scroll Logic - DISABLED per user request
    React.useEffect(() => {
        // if (scrollBottomRef.current) {
        //     scrollBottomRef.current.scrollIntoView({ behavior: "smooth" })
        // }
    }, [messages])

    // Enable auto-scroll when user selects a session (reset view)
    React.useEffect(() => {
        if (selectedSession) {
            setTimeout(scrollToBottom, 100)
        }
    }, [selectedSession])

    const handleScroll = (e: React.UIEvent<HTMLDivElement>) => {
        const target = e.currentTarget
        const isAtBottom = target.scrollHeight - target.scrollTop <= target.clientHeight + 100
        setShowScrollButton(!isAtBottom)
    }

    // Handle Manual Send
    const handleSend = async () => {
        if (!selectedSession || !inputText.trim()) return

        setIsSending(true)
        const textToSend = inputText
        setInputText("") // Optimistic clear

        try {
            console.log("Sending message to:", selectedSession)
            const res = await fetch('/api/chat/send', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({
                    phone_number: selectedSession,
                    message: textToSend,
                    session_id: selectedSession
                })
            })

            const data = await res.json()
            console.log("Send API Response:", data)

            if (!res.ok) {
                console.error("Send Failed:", data)
                alert(`Message failed: ${data.error || "Unknown Error"}`)
                // Revert text if failed (optional, but good UX)
                setInputText(textToSend)
            } else {
                // Since sending auto-pauses AI, we should update status
                setIsAiPaused(true)
                await fetchHistory()
            }
        } catch (error) {
            console.error("Send Network Error", error)
            alert("Network Error: Could not reach server.")
            setInputText(textToSend)
        } finally {
            setIsSending(false)
        }
    }

    // Convert Flat Rows to Visual Bubbles for the active session
    const getActiveVisualMessages = () => {
        if (!selectedSession) return []

        const sessionRows = messages.filter(m =>
            (m.phone_number === selectedSession) || (m.session_id === selectedSession)
        );

        const visual: VisualMessage[] = []

        sessionRows.forEach(row => {
            // Push User Message
            if (row.user_message) {
                visual.push({
                    id: `${row.id}_user`,
                    role: "user",
                    content: row.user_message,
                    timestamp: row.created_at || new Date().toISOString(),
                    status: row.status,
                    mediaUrl: (row.message_meta as any)?.media_url || (row.message_meta as any)?.image_url // eslint-disable-line @typescript-eslint/no-explicit-any
                })
            }
            // Push AI/Manual Response (if exists)
            if (row.ai_response) {
                const isManual = row.intent === "Manual Override"
                visual.push({
                    id: `${row.id}_ai`,
                    role: "assistant",
                    content: row.ai_response,
                    timestamp: row.created_at || new Date().toISOString(),
                    sentiment: row.sentiment || undefined,
                    isManual: isManual,
                    status: row.status,
                    mediaUrl: (row.message_meta as any)?.media_url || (row.message_meta as any)?.image_url // eslint-disable-line @typescript-eslint/no-explicit-any
                })
            }
        })

        return visual
    }

    const activeVisuals = getActiveVisualMessages()

    return (
        <div className="h-full w-full flex flex-col overflow-hidden bg-transparent">

            {/* Fixed Header Section (Standardized) */}
            <div className="flex-none px-4 md:px-8 pt-10 pb-6 bg-transparent space-y-4 relative z-20">
                <div className="flex flex-col md:flex-row items-start md:items-center justify-between gap-4">
                    <div>
                        <h2 className="text-xl md:text-2xl font-bold tracking-tight text-white flex items-center gap-2">
                            <MessageSquare className="h-6 w-6 text-violet-500" />
                            Live Chat Feed
                        </h2>
                        <p className="text-muted-foreground mt-1 text-xs md:text-sm">Monitor and intervene in Aditi&apos;s conversations.</p>
                    </div>
                </div>
            </div>

            {/* Content Section - Main Chat Interface */}
            <div className="w-full px-4 md:px-8 pb-4 h-[calc(100vh-140px)]">
                {/* The "Card" Container */}
                <div className="w-full h-full bg-[#0F131E] border border-white/10 rounded-xl overflow-hidden flex relative shadow-2xl">

                    {/* 1. SIDEBAR (Left) - Fixed Width Flex Item */}
                    <div className="hidden md:flex flex-col w-[300px] shrink-0 border-r border-white/10 bg-[#0B0F19] h-full relative z-10">
                        {/* Sidebar Header + Search */}
                        <div className="flex flex-col border-b border-white/10 bg-[#111623] shrink-0 shadow-sm relative z-20">
                            <div className="h-16 flex items-center px-5 justify-between">
                                <h2 className="text-sm font-bold text-white tracking-wide uppercase flex items-center gap-2">
                                    <Users className="h-4 w-4 text-violet-400" /> Inquiries <span className="text-violet-200/50">({sessions.length})</span>
                                </h2>
                            </div>
                            <div className="px-4 pb-3">
                                <div className="relative">
                                    <Search className="absolute left-3 top-1/2 -translate-y-1/2 h-3.5 w-3.5 text-slate-500" />
                                    <input
                                        className="w-full bg-white/5 border border-white/10 rounded-lg py-1.5 pl-9 pr-3 text-xs text-white placeholder:text-slate-600 focus:outline-none focus:border-violet-500/50"
                                        placeholder="Search by name or phone..."
                                        value={sessionSearch}
                                        onChange={(e) => setSessionSearch(e.target.value)}
                                    />
                                </div>
                            </div>
                        </div>
                        <div className="flex-1 min-h-0 w-full overflow-y-auto custom-scrollbar">
                            <div className="p-0">
                                {loading ? (
                                    <div className="text-center p-8 text-xs text-slate-500">Syncing...</div>
                                ) : sessions.length === 0 ? (
                                    <div className="text-center p-8 text-xs text-slate-500">No active chats.</div>
                                ) : (
                                    sessions
                                        .filter(sid => {
                                            const name = (userMap[sid] || "").toLowerCase();
                                            const phone = sid.toLowerCase();
                                            const search = sessionSearch.toLowerCase();
                                            return name.includes(search) || phone.includes(search);
                                        })
                                        .map((sessionId, i) => {
                                            // Get latest sentiment for this session
                                            const sessionMsgs = messages.filter(m => m.phone_number === sessionId || m.session_id === sessionId);
                                            const latestMsg = sessionMsgs[sessionMsgs.length - 1];
                                            const sentiment = latestMsg?.sentiment?.toLowerCase();
                                            // Real functional unread logic
                                            const isUnread = sessionMsgs.some(m => m.user_message && !m.is_read);

                                            return (
                                                <div
                                                    key={i}
                                                    onClick={() => setSelectedSession(sessionId)}
                                                    className={`flex items-center gap-3 p-4 border-b border-white/5 cursor-pointer transition-all ${selectedSession === sessionId ? "bg-white/10 border-l-2 border-l-violet-500" : "hover:bg-white/[0.05] border-l-2 border-l-transparent"}`}
                                                >
                                                    <div className="relative shrink-0">
                                                        <Avatar className="h-10 w-10 border border-white/10">
                                                            <AvatarFallback className="bg-gradient-to-br from-slate-800 to-slate-900 text-slate-300 text-xs font-bold">
                                                                {(userMap[sessionId] || sessionId).charAt(0)}
                                                            </AvatarFallback>
                                                        </Avatar>
                                                        {/* Sentiment Indicator (Keep it small and clean on avatar) */}
                                                        {sentiment && (
                                                            <div className={`absolute -bottom-0.5 -right-0.5 h-3.5 w-3.5 rounded-full border-2 border-[#0B0F19] flex items-center justify-center
                                                                ${sentiment === 'positive' ? 'bg-emerald-500' : sentiment === 'negative' ? 'bg-red-500' : 'bg-slate-500'}`}>
                                                                {sentiment === 'positive' ? <Smile className="h-2 w-2 text-white" /> : sentiment === 'negative' ? <ThumbsDown className="h-2 w-2 text-white" /> : null}
                                                            </div>
                                                        )}
                                                    </div>

                                                    <div className="flex flex-col overflow-hidden gap-0.5 flex-1 text-left">
                                                        <div className="flex justify-between items-center w-full">
                                                            <span className={`text-sm tracking-tight ${selectedSession === sessionId ? "text-white font-semibold" : isUnread ? "text-white font-bold" : "text-slate-300 font-medium"} truncate`}>
                                                                {userMap[sessionId] || sessionId}
                                                            </span>
                                                            {latestMsg?.created_at && (
                                                                <span className={`text-[10px] ${isUnread ? "text-emerald-500 font-bold" : "text-slate-500"}`}>
                                                                    {new Date(latestMsg.created_at).toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })}
                                                                </span>
                                                            )}
                                                        </div>
                                                        <div className="flex justify-between items-center w-full gap-2">
                                                            <span className={`text-[11px] truncate flex-1 ${isUnread ? "text-slate-200 font-semibold" : "text-slate-500"}`}>
                                                                {latestMsg?.user_message || latestMsg?.ai_response || "No messages"}
                                                            </span>
                                                            {/* WhatsApp Style Unread Badge */}
                                                            {isUnread && (
                                                                <div className="min-w-[18px] h-[18px] bg-emerald-500 rounded-full flex items-center justify-center px-1">
                                                                    <span className="text-[10px] text-white font-bold leading-none">
                                                                        {sessionMsgs.filter(m => m.user_message && !m.is_read).length}
                                                                    </span>
                                                                </div>
                                                            )}
                                                        </div>
                                                    </div>
                                                </div>
                                            );
                                        })
                                )}
                            </div>
                        </div>
                    </div>

                    {/* 2. MAIN CHAT AREA (Right) */}
                    <div className="flex-1 flex flex-col min-w-0 bg-[#0F131E] h-full relative">
                        {/* Floating Chat Header - Glassmorphism */}
                        <div className="absolute top-0 left-0 right-0 h-16 border-b border-white/10 flex items-center justify-between px-6 bg-[#0F131E]/60 backdrop-blur-lg shrink-0 z-30 shadow-sm">
                            <div className="flex items-center gap-4">
                                {selectedSession ? (
                                    <>
                                        <div className="relative">
                                            <Avatar className="h-9 w-9 border border-white/10">
                                                <AvatarFallback className="bg-violet-600 text-white font-bold">
                                                    {userMap[selectedSession!]?.charAt(0) || selectedSession!.charAt(0)}
                                                </AvatarFallback>
                                            </Avatar>
                                            <span className={`absolute -bottom-0.5 -right-0.5 flex h-2.5 w-2.5 rounded-full border-2 border-[#111623] ${isAiPaused ? "bg-red-500" : "bg-emerald-500"}`} />
                                        </div>

                                        <div>
                                            <h3 className="text-sm font-bold text-white flex items-center gap-2">
                                                {userMap[selectedSession!] || selectedSession}
                                            </h3>
                                            <p className="text-[10px] font-medium flex items-center gap-1.5">
                                                {isAiPaused ? (
                                                    <span className="text-amber-400 flex items-center gap-1"><Hand className="h-3 w-3" /> Manual Control</span>
                                                ) : (
                                                    <span className="text-emerald-400 flex items-center gap-1"><Sparkles className="h-3 w-3" /> Aditi Active</span>
                                                )}
                                            </p>
                                        </div>
                                    </>
                                ) : (
                                    <div className="flex items-center gap-2 text-slate-400">
                                        <MessageSquare className="h-5 w-5" />
                                        <span className="text-sm font-medium">Select a conversation</span>
                                    </div>
                                )}
                            </div>

                            <div className="flex items-center gap-3">
                                {isAiPaused && (
                                    <Button
                                        size="sm"
                                        variant="ghost"
                                        className="h-8 text-emerald-400 hover:text-emerald-300 hover:bg-emerald-500/10 text-xs uppercase tracking-wider font-bold border border-emerald-500/20"
                                        onClick={handleResume}
                                    >
                                        <Check className="mr-1.5 h-3 w-3" /> Resume AI
                                    </Button>
                                )}
                            </div>
                        </div>

                        {/* Messages Body */}
                        <div className="flex-1 relative min-h-0 bg-transparent overflow-hidden">
                            {/* Scroll to Bottom Button - Centered & Small */}
                            {selectedSession && showScrollButton && (
                                <button
                                    onClick={scrollToBottom}
                                    className="absolute bottom-24 left-1/2 -translate-x-1/2 z-50 bg-violet-600/90 hover:bg-violet-700 text-white p-2 rounded-full shadow-[0_4px_15px_rgba(0,0,0,0.3)] border border-white/10 transition-all hover:scale-110 active:scale-95 flex items-center justify-center backdrop-blur-sm"
                                    title="Scroll to bottom"
                                >
                                    <ChevronDown className="h-4 w-4" />
                                </button>
                            )}

                            <div
                                ref={scrollContainerRef}
                                onScroll={handleScroll}
                                className="h-full overflow-y-auto custom-scrollbar p-4 pt-20 pb-24"
                            >

                                <div className="max-w-3xl mx-auto space-y-6 py-2">
                                    {activeVisuals.length === 0 && !loading && (
                                        <div className="flex flex-col items-center justify-center p-12 opacity-20">
                                            <MessageSquare className="h-16 w-16 mb-4" />
                                            <p>No messages yet</p>
                                        </div>
                                    )}

                                    {(() => {
                                        let lastDate = "";
                                        return activeVisuals.map((msg) => {
                                            const isAi = msg.role === "assistant";
                                            const currentDate = new Date(msg.timestamp).toDateString();
                                            const showDivider = currentDate !== lastDate;
                                            lastDate = currentDate;

                                            return (
                                                <React.Fragment key={msg.id}>
                                                    {showDivider && (
                                                        <div className="flex justify-center my-6">
                                                            <div className="bg-[#111623]/80 backdrop-blur-md border border-white/5 py-1 px-4 rounded-lg text-[10px] font-bold text-slate-400 uppercase tracking-widest shadow-sm">
                                                                {formatDateDivider(msg.timestamp)}
                                                            </div>
                                                        </div>
                                                    )}
                                                    <div className={`flex ${isAi ? "justify-end" : "justify-start"}`}>
                                                        <div className={`flex gap-3 max-w-[85%] ${isAi ? "flex-row-reverse" : "flex-row"}`}>

                                                            {/* Avatar for Message */}
                                                            {!isAi && (
                                                                <Avatar className="h-8 w-8 border border-white/5 mt-1 shrink-0 opacity-70">
                                                                    <AvatarFallback className="bg-slate-800 text-slate-400 text-[10px]">{userMap[selectedSession!]?.charAt(0) || "U"}</AvatarFallback>
                                                                </Avatar>
                                                            )}
                                                            {isAi && (
                                                                <Avatar className="h-8 w-8 border border-white/5 mt-1 shrink-0 opacity-70">
                                                                    <AvatarImage src="/avatars/aditi.png" />
                                                                    <AvatarFallback className="bg-violet-900/50 text-violet-300 text-[10px]">AI</AvatarFallback>
                                                                </Avatar>
                                                            )}

                                                            <div className={`flex flex-col ${isAi ? "items-end" : "items-start"} min-w-[100px]`}>
                                                                <div className={`py-3 px-5 text-sm leading-relaxed whitespace-pre-wrap shadow-lg ${isAi
                                                                    ? (msg.isManual
                                                                        ? "bg-blue-600 text-white rounded-2xl rounded-tr-sm"
                                                                        : "bg-gradient-to-br from-[#2E3545] to-[#232936] text-slate-100 border border-white/5 rounded-2xl rounded-tr-sm")
                                                                    : "bg-[#1E293B] text-slate-200 border border-white/5 rounded-2xl rounded-tl-sm"
                                                                    }`}>
                                                                    {msg.mediaUrl && (
                                                                        <div className="mb-2 rounded-lg overflow-hidden border border-white/10 bg-black/20">
                                                                            <img src={msg.mediaUrl} alt="Message attachment" className="max-w-full h-auto object-contain max-h-[300px]" />
                                                                        </div>
                                                                    )}
                                                                    {msg.content}
                                                                </div>
                                                                <div className="flex items-center gap-2 mt-1 px-1 opacity-50 text-[10px]">
                                                                    <span className="font-medium text-slate-400">
                                                                        {msg.timestamp ? new Date(msg.timestamp).toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' }) : "Just now"}
                                                                    </span>
                                                                    {msg.role === "assistant" && (
                                                                        <span>
                                                                            {msg.status === 'read' ? (
                                                                                <CheckCheck className="h-3.5 w-3.5 text-[#53bdeb]" strokeWidth={2.5} />
                                                                            ) : msg.status === 'delivered' ? (
                                                                                <CheckCheck className="h-3.5 w-3.5 text-slate-400" strokeWidth={2.5} />
                                                                            ) : (
                                                                                <Check className="h-3.5 w-3.5 text-slate-400" strokeWidth={2.5} />
                                                                            )}
                                                                        </span>
                                                                    )}
                                                                </div>
                                                            </div>
                                                        </div>
                                                    </div>
                                                </React.Fragment>
                                            );
                                        });
                                    })()}
                                    <div ref={scrollBottomRef}></div>
                                </div>
                            </div>
                        </div>

                        {/* Input Area */}
                        {/* Floating Input Area - True Floating Effect (No Blur Bar) */}
                        <div className="absolute bottom-0 left-0 right-0 p-4 bg-transparent shrink-0 z-30 pointer-events-none">
                            <div className="max-w-4xl mx-auto flex items-center gap-3 pointer-events-auto">
                                {/* The Input Pill */}
                                <div className="flex-1 flex items-center bg-[#111623] border border-white/10 rounded-full px-4 min-h-[52px] shadow-[0_10px_40px_rgba(0,0,0,0.5)] focus-within:border-violet-500/50 focus-within:ring-1 focus-within:ring-violet-500/30 transition-all duration-300 hover:translate-y-[-2px]">
                                    <div className="flex items-center gap-1 sm:gap-2 mr-2">
                                        <button className="p-2 text-slate-500 hover:text-violet-400 transition-colors shrink-0">
                                            <Smile className="h-6 w-6" />
                                        </button>
                                        <button className="p-2 text-slate-500 hover:text-violet-400 transition-colors shrink-0">
                                            <Plus className="h-6 w-6" />
                                        </button>
                                    </div>

                                    <input
                                        className="flex-1 bg-transparent text-sm md:text-base text-white focus:outline-none placeholder:text-slate-600 py-3"
                                        placeholder={selectedSession ? (isAiPaused ? "Type a reply to send manually..." : "Type to interrupt and take over...") : "Select a conversation"}
                                        disabled={!selectedSession || isSending}
                                        value={inputText}
                                        onChange={(e) => setInputText(e.target.value)}
                                        onKeyDown={(e) => e.key === 'Enter' && handleSend()}
                                    />

                                    <div className="hidden sm:flex items-center ml-2">
                                        <button className="p-2 text-slate-500 hover:text-violet-400 transition-colors shrink-0">
                                            <ImageIcon className="h-5 w-5" />
                                        </button>
                                    </div>
                                </div>

                                {/* Floating Circular Send Button - Completely Outside */}
                                <Button
                                    disabled={!selectedSession || isSending || !inputText.trim()}
                                    className={`rounded-full h-[52px] w-[52px] shrink-0 p-0 shadow-2xl flex items-center justify-center transition-all duration-300 ${!inputText.trim()
                                        ? "bg-[#111623] text-slate-600 cursor-not-allowed border border-white/5"
                                        : "bg-violet-600 hover:bg-violet-700 text-white hover:scale-105 active:scale-95 shadow-violet-900/30"
                                        }`}
                                    onClick={handleSend}
                                >
                                    {isSending ? (
                                        <div className="h-5 w-5 border-2 border-white/20 border-t-white rounded-full animate-spin" />
                                    ) : (
                                        <Send className="h-5 w-5 ml-0.5" />
                                    )}
                                </Button>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    )
}

"use server"

import { createServerClient } from "@/lib/supabase-server"
import { revalidatePath } from "next/cache"
import { getAuthenticatedInstituteId } from "@/lib/auth-utils"

export async function updateLead(leadId: string, data: any) {
    const supabase = await createServerClient()
    const institute_id = await getAuthenticatedInstituteId(supabase)

    const { error } = await supabase
        .from("leads")
        .update(data)
        .eq("id", leadId)
        .eq("institute_id", institute_id)

    if (error) {
        throw new Error(error.message)
    }

    revalidatePath("/workspace/leads")
    return { success: true }
}

export async function createLead(data: any) {
    const supabase = await createServerClient()

    // Resilient identification
    const institute_id = await getAuthenticatedInstituteId(supabase)

    const { data: lead, error } = await supabase
        .from("leads")
        .insert({
            ...data,
            institute_id
        })
        .select()
        .single()

    if (error) {
        throw new Error(error.message)
    }

    revalidatePath("/workspace/leads")
    return { success: true, data: lead }
}

export async function getLeads() {
    const supabase = await createServerClient()
    const institute_id = await getAuthenticatedInstituteId(supabase)

    const { data, error } = await supabase
        .from("leads")
        .select("*")
        .eq("institute_id", institute_id)
        .order("created_at", { ascending: false })

    if (error) {
        throw new Error(error.message)
    }

    return data
}

export async function deleteLead(leadId: string) {
    const supabase = await createServerClient()
    const institute_id = await getAuthenticatedInstituteId(supabase)

    const { error } = await supabase
        .from("leads")
        .delete()
        .eq("id", leadId)
        .eq("institute_id", institute_id)

    if (error) {
        throw new Error(error.message)
    }

    revalidatePath("/workspace/leads")
    return { success: true }
}

"use client"

import * as React from "react"
import { createClient } from "@/lib/supabase"
import { Database } from "@/lib/database.types" // Use generated types
import {
    Table,
    TableBody,
    TableCell,
    TableHead,
    TableHeader,
    TableRow,
} from "@/components/ui/table"
import { Badge } from "@/components/ui/badge"
import { Button } from "@/components/ui/button"
import {
    Card,
    CardContent,
    CardDescription,
    CardHeader,
    CardTitle,
} from "@/components/ui/card"
import {
    DropdownMenu,
    DropdownMenuContent,
    DropdownMenuItem,
    DropdownMenuLabel,
    DropdownMenuSeparator,
    DropdownMenuTrigger,
} from "@/components/ui/dropdown-menu"
import { MoreHorizontal, Plus, Calendar, Phone, MapPin, Sparkles, ArrowUpRight, Trash2, Users } from "lucide-react"
import { Avatar, AvatarFallback, AvatarImage } from "@/components/ui/avatar"
import {
    Dialog,
    DialogContent,
    DialogDescription,
    DialogHeader,
    DialogTitle,
    DialogTrigger,
} from "@/components/ui/dialog"
import { ScrollArea } from "@/components/ui/scroll-area"
import { Input } from "@/components/ui/input"
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select"
import { updateLead, createLead, deleteLead } from "./actions"
import { getCourses } from "../courses/actions"
import { format } from "date-fns" // We might need to install date-fns or use native Intl

type Lead = Database["public"]["Tables"]["leads"]["Row"]

export default function LeadsPage() {
    const [leads, setLeads] = React.useState<Lead[]>([])
    const [loading, setLoading] = React.useState(true)
    const [selectedLead, setSelectedLead] = React.useState<Lead | null>(null)
    const [dialogOpen, setDialogOpen] = React.useState(false)
    const [isEditing, setIsEditing] = React.useState(false)
    const [isCreating, setIsCreating] = React.useState(false)
    const [courses, setCourses] = React.useState<any[]>([])
    const [formData, setFormData] = React.useState<Partial<Lead>>({})

    const handleEditToggle = () => {
        if (!isEditing && selectedLead) {
            setFormData(selectedLead)
        }
        setIsEditing(!isEditing)
    }

    const handleSave = async () => {
        try {
            // Filter out system fields that shouldn't be updated
            const { id, created_at, updated_at, institute_id, ...updateData } = formData

            if (isCreating) {
                const response = await createLead(updateData)
                if (response.success && response.data) {
                    setLeads([response.data, ...leads])
                    setDialogOpen(false)
                }
            } else if (selectedLead) {
                await updateLead(selectedLead.id, updateData)
                // Optimistic update
                const updatedLead = { ...selectedLead, ...updateData } as Lead
                setSelectedLead(updatedLead)
                setLeads(leads.map(l => l.id === updatedLead.id ? updatedLead : l))
                setIsEditing(false)
            }
        } catch (error) {
            console.error("Failed to save lead:", error)
        }
    }

    const handleDelete = async () => {
        if (!selectedLead) return
        if (!confirm("Are you sure you want to delete this lead? This action cannot be undone.")) return

        try {
            await deleteLead(selectedLead.id)
            setLeads(leads.filter(l => l.id !== selectedLead.id))
            setDialogOpen(false)
        } catch (error) {
            console.error("Failed to delete lead:", error)
            alert("Failed to delete lead. Please try again.")
        }
    }

    const handleNewLead = () => {
        setIsCreating(true)
        setIsEditing(true)
        setFormData({
            status: 'NEW',
            source: 'DIRECT',
            visit_type: 'offline'
        })
        setDialogOpen(true)
    }

    const getSafeDateValue = (dateStr: string | null | undefined, includeTime = false) => {
        if (!dateStr) return ""
        try {
            const date = new Date(dateStr)
            if (isNaN(date.getTime())) return ""
            const iso = date.toISOString()
            return includeTime ? iso.slice(0, 16) : iso.split('T')[0]
        } catch (e) {
            return ""
        }
    }

    React.useEffect(() => {
        if (!dialogOpen) {
            setIsEditing(false)
            setIsCreating(false)
        }
    }, [dialogOpen])

    React.useEffect(() => {
        const fetchInitialData = async () => {
            setLoading(true)
            const supabase = createClient()

            try {
                // Fetch Leads and Courses in parallel
                const [leadsResponse, coursesData] = await Promise.all([
                    supabase.from("leads").select("*").order("updated_at", { ascending: false }).limit(50),
                    getCourses()
                ])

                if (leadsResponse.error) {
                    console.error("Supabase Error:", leadsResponse.error)
                } else {
                    // DEMO: If explicit fetch has fewer items, inject dummy data to test scroll
                    const actualLeads = leadsResponse.data || []
                    if (actualLeads.length < 50) {
                        const dummyLeads = Array.from({ length: 50 }).map((_, i) => ({
                            id: `dummy-${i}`,
                            name: `Demo Student ${i + 1}`,
                            phone: `99999${String(i).padStart(5, '0')}`,
                            city: i % 2 === 0 ? "Indore" : "Bhopal",
                            source: i % 3 === 0 ? "INSTAGRAM" : "WALK_IN",
                            interested_course: i % 2 === 0 ? "Data Science" : "Digital Marketing",
                            lead_score: 40 + (i * 2),
                            status: i % 4 === 0 ? "HOT_LEAD" : "NEW",
                            next_followup: new Date().toISOString(),
                            created_at: new Date().toISOString()
                        })) as any
                        setLeads([...actualLeads, ...dummyLeads])
                    } else {
                        setLeads(actualLeads)
                    }
                }

                setCourses(coursesData || [])
            } catch (error) {
                console.error("Failed to fetch initial data:", error)
            } finally {
                setLoading(false)
            }
        }

        fetchInitialData()
    }, [])

    const getStatusBadge = (status: string | null) => {
        const s = (status || 'fresh').toLowerCase()
        if (s.includes('converted') || s.includes('admitted')) return <Badge className="bg-green-500/10 text-green-400 border-green-500/20">Converted</Badge>
        if (s.includes('visit') || s.includes('scheduled')) return <Badge className="bg-purple-500/10 text-purple-400 border-purple-500/20">Visit Planned</Badge>
        if (s.includes('dead') || s.includes('lost')) return <Badge className="bg-red-500/10 text-red-400 border-red-500/20">Lost</Badge>
        if (s.includes('hot')) return <Badge className="bg-orange-500/10 text-orange-400 border-orange-500/20">Hot Lead</Badge>
        return <Badge variant="outline" className="text-slate-400 border-slate-700">{status || 'Fresh'}</Badge>
    }

    const formatDate = (dateString: string | null) => {
        if (!dateString) return <span className="text-slate-600">N/A</span>
        try {
            return new Date(dateString).toLocaleDateString('en-IN', { month: 'short', day: 'numeric', hour: '2-digit', minute: '2-digit' })
        } catch (e) {
            return <span className="text-red-400">Invalid Date</span>
        }
    }

    return (
        <>
            <div className="flex-1 w-full flex flex-col overflow-hidden bg-transparent">
                {/* Header Section (Transparent) */}
                <div className="flex-none px-4 md:px-8 py-6 flex flex-col md:flex-row items-start md:items-center justify-between gap-4 bg-transparent relative z-20">
                    <div>
                        <h2 className="text-2xl md:text-3xl font-bold tracking-tight text-white flex items-center gap-3">
                            <Users className="h-8 w-8 text-emerald-500" />
                            Lead Management
                        </h2>
                        <p className="text-muted-foreground mt-1 text-sm md:text-base">Track Visits, Follow-ups, and Admissions.</p>
                    </div>
                    <Button className="w-full md:w-auto bg-white text-black hover:bg-slate-200 h-9 px-4 text-xs font-bold rounded-xl shadow-lg" onClick={handleNewLead}>
                        <Plus className="mr-2 h-4 w-4 stroke-[3px]" /> Add Manual Lead
                    </Button>
                </div>


                {/* Content Section (Explicit constrained height to ensure scroll) */}
                <div className="w-full px-4 md:px-8 pb-4 h-[calc(100vh-140px)]">
                    <div className="w-full h-full bg-gradient-to-br from-violet-500/10 to-transparent border border-white/10 border-violet-500/20 shadow-2xl rounded-xl overflow-hidden flex flex-col relative">
                        {/* Scrollbar Patch: Covers the top-right scrollbar track for a cleaner look */}
                        <div className="absolute top-0 right-0 h-12 w-4 bg-[#0B0F19]/75 backdrop-blur-md z-40 border-b border-white/10" />

                        <div className="flex-1 overflow-y-auto custom-scrollbar relative">
                            <table className="w-full text-sm text-left border-collapse min-w-[1000px]">
                                <thead className="sticky top-0 z-20 bg-[#0B0F19]/75 backdrop-blur-md border-b border-white/5 shadow-sm">
                                    <tr className="text-left">
                                        <th className="h-12 px-6 font-medium text-slate-400 text-xs uppercase tracking-wider">Candidate</th>
                                        <th className="h-12 px-6 font-medium text-slate-400 text-xs uppercase tracking-wider w-[180px]">Source / City</th>
                                        <th className="h-12 px-6 font-medium text-slate-400 text-xs uppercase tracking-wider">Interest</th>
                                        <th className="h-12 px-6 font-medium text-slate-400 text-xs uppercase tracking-wider">Score</th>
                                        <th className="h-12 px-6 font-medium text-slate-400 text-xs uppercase tracking-wider">Next Action</th>
                                        <th className="h-12 px-6 font-medium text-slate-400 text-xs uppercase tracking-wider text-center">Status</th>
                                        <th className="h-12 px-6 font-medium text-slate-400 text-xs uppercase tracking-wider text-right">Actions</th>
                                    </tr>
                                </thead>
                                <tbody className="divide-y divide-white/5">
                                    {loading ? (
                                        <tr>
                                            <td colSpan={7} className="h-32 text-center text-slate-500 text-sm">Syncing with Brain...</td>
                                        </tr>
                                    ) : leads.map((lead) => (
                                        <tr key={lead.id} className="hover:bg-white/[0.08] group cursor-pointer border-b border-white/[0.03] transition-all duration-150" onClick={() => { setSelectedLead(lead); setDialogOpen(true) }}>
