                                                    </Select>
                                                ) : (
                                                    <div className="text-xs font-medium text-white capitalize">{selectedLead?.visit_type || "N/A"}</div>
                                                )}
                                            </div>
                                        </div>
                                    </section>

                                    {/* Footer Note */}
                                    {!isCreating && (
                                        <div className="pt-8 text-center">
                                            <p className="text-[10px] text-slate-600 uppercase tracking-widest">Lead ID: {selectedLead?.id}</p>
                                        </div>
                                    )}
                                </div>
                            </ScrollArea>
                        </div>
                    )}
                </DialogContent>
            </Dialog >
        </>
    )
}

"use server"

import { createServerClient } from "@/lib/supabase-server"
import { revalidatePath } from "next/cache"
import { getAuthenticatedInstituteId } from "@/lib/auth-utils"

export async function getStudents() {
    const supabase = await createServerClient()
    const institute_id = await getAuthenticatedInstituteId(supabase)

    const { data, error } = await supabase
        .from("students")
        .select("*")
        .eq("institute_id", institute_id)
        .order("name", { ascending: true })

    if (error) {
        throw new Error(error.message)
    }

    return data
}

export async function deleteStudent(studentId: string) {
    const supabase = await createServerClient()
    const institute_id = await getAuthenticatedInstituteId(supabase)

    const { error } = await supabase
        .from("students")
        .delete()
        .eq("id", studentId)
        .eq("institute_id", institute_id)

    if (error) {
        throw new Error(error.message)
    }

    revalidatePath("/workspace/students")
    return { success: true }
}

export async function updateStudent(studentId: string, data: any) {
    const supabase = await createServerClient()
    const institute_id = await getAuthenticatedInstituteId(supabase)

    const { error } = await supabase
        .from("students")
        .update(data)
        .eq("id", studentId)
        .eq("institute_id", institute_id)

    if (error) {
        throw new Error(error.message)
    }

    revalidatePath("/workspace/students")
    return { success: true }
}

"use client"

import * as React from "react"
import { createClient } from "@/lib/supabase"
import { Badge } from "@/components/ui/badge"
import { Button } from "@/components/ui/button"
import {
    Card,
    CardContent,
    CardHeader,
    CardTitle,
} from "@/components/ui/card"
import { GraduationCap, Phone, User, Search, Filter, Trash2, ArrowUpRight } from "lucide-react"
import { Avatar, AvatarFallback } from "@/components/ui/avatar"
import { Input } from "@/components/ui/input"
import {
    Dialog,
    DialogContent,
    DialogHeader,
    DialogTitle,
} from "@/components/ui/dialog"
import { getStudents, deleteStudent, updateStudent } from "./actions"

export default function StudentsPage() {
    const [students, setStudents] = React.useState<any[]>([])
    const [loading, setLoading] = React.useState(true)
    const [searchQuery, setSearchQuery] = React.useState("")
    const [selectedStudent, setSelectedStudent] = React.useState<any | null>(null)
    const [isDialogOpen, setIsDialogOpen] = React.useState(false)
    const [formData, setFormData] = React.useState<any>({})
    const [isSaving, setIsSaving] = React.useState(false)

    React.useEffect(() => {
        const fetchData = async () => {
            setLoading(true)
            try {
                const data = await getStudents()
                setStudents(data || [])
            } catch (error) {
                console.error("Failed to fetch students:", error)
            } finally {
                setLoading(false)
            }
        }
        fetchData()
    }, [])

    const handleDelete = async (id: string, e?: React.MouseEvent) => {
        if (e) e.stopPropagation()
        if (!confirm("Are you sure you want to delete this student record?")) return
        try {
            await deleteStudent(id)
            setStudents(students.filter(s => s.id !== id))
            if (selectedStudent?.id === id) setIsDialogOpen(false)
        } catch (error) {
            console.error("Failed to delete student:", error)
        }
    }

    const handleEdit = (student: any) => {
        setSelectedStudent(student)
        setFormData({
            name: student.name || "",
            phone: student.phone || "",
            lead_status: student.lead_status || "Enrolled"
        })
        setIsDialogOpen(true)
    }

    const handleSave = async () => {
        if (!selectedStudent) return
        setIsSaving(true)
        try {
            await updateStudent(selectedStudent.id, formData)
            setStudents(students.map(s => s.id === selectedStudent.id ? { ...s, ...formData } : s))
            setIsDialogOpen(false)
        } catch (error) {
            console.error("Failed to update student:", error)
        } finally {
            setIsSaving(false)
        }
    }

    const filteredStudents = students.filter(s =>
    (s.name?.toLowerCase().includes(searchQuery.toLowerCase()) ||
        s.phone?.includes(searchQuery))
    )

    return (
        <div className="h-full w-full flex flex-col overflow-hidden bg-transparent">

            {/* Fixed Header Section (Includes Title + Search) */}
            <div className="flex-none px-4 md:px-8 py-6 bg-transparent space-y-4 relative z-20">
                <div className="flex flex-col md:flex-row items-start md:items-center justify-between gap-4">
                    <div>
                        <h2 className="text-2xl md:text-3xl font-bold tracking-tight text-white flex items-center gap-3">
                            <GraduationCap className="h-8 w-8 text-emerald-500" />
                            Active Students
                        </h2>
                        <p className="text-muted-foreground mt-1 text-sm md:text-base">Manage your enrolled student database.</p>
                    </div>
                </div>

                {/* Search & Filters Row (Integrated into Header to keep Table area clean) */}
                <div className="flex items-center gap-3">
                    <div className="relative flex-1 max-w-md">
                        <Search className="absolute left-3 top-1/2 -translate-y-1/2 h-4 w-4 text-slate-500" />
                        <Input
                            placeholder="Search students..."
                            className="pl-10 h-10 bg-white/5 border-white/10 text-white focus:bg-white/10 transition-all rounded-xl"
                            value={searchQuery}
                            onChange={(e) => setSearchQuery(e.target.value)}
                        />
                    </div>
                    <Button
                        variant="outline"
                        className="h-10 border-white/10 text-slate-300 hover:bg-white/5 active:scale-95 transition-transform rounded-xl"
                        onClick={() => alert("Advanced filtering is coming soon!")}
                    >
                        <Filter className="mr-2 h-4 w-4" /> Filters
                    </Button>
                </div>
            </div>

            {/* Content Section (Exact Table Layout from LeadsPage) */}
            {/* Adjusted height calculation to account for taller header due to search bar: 140px + 60px approx = 200px */}
            <div className="w-full px-4 md:px-8 pb-4 h-[calc(100vh-200px)]">
                <div className="w-full h-full bg-white/5 border border-white/10 rounded-xl overflow-hidden flex flex-col relative">

                    {/* Scrollbar Patch: Dark Glass Effect */}
                    <div className="absolute top-0 right-0 h-12 w-4 bg-[#0B0F19]/75 backdrop-blur-md z-40 border-b border-white/10" />

                    <div className="flex-1 overflow-y-auto custom-scrollbar relative">
                        <table className="w-full text-sm text-left border-collapse min-w-[1000px]">
                            <thead className="sticky top-0 z-20 bg-[#0B0F19]/75 backdrop-blur-md border-b border-white/5 shadow-sm">
                                <tr className="text-left">
                                    <th className="h-12 px-6 font-medium text-slate-400 text-xs uppercase tracking-wider">Student</th>
                                    <th className="h-12 px-6 font-medium text-slate-400 text-xs uppercase tracking-wider">Contact</th>
                                    <th className="h-12 px-6 font-medium text-slate-400 text-xs uppercase tracking-wider">Status</th>
                                    <th className="h-12 px-6 font-medium text-slate-400 text-xs uppercase tracking-wider">Joined Date</th>
                                    <th className="h-12 px-6 font-medium text-slate-400 text-xs uppercase tracking-wider text-right">Actions</th>
                                </tr>
                            </thead>
                            <tbody className="divide-y divide-white/5">
                                {loading ? (
                                    <tr>
                                        <td colSpan={5} className="h-32 text-center text-slate-500">Syncing with Brain...</td>
                                    </tr>
                                ) : filteredStudents.length === 0 ? (
                                    <tr>
                                        <td colSpan={5} className="h-32 text-center text-slate-500">No students found.</td>
                                    </tr>
                                ) : (
                                    filteredStudents.map((student) => (
                                        <tr
                                            key={student.id}
                                            className="hover:bg-white/[0.08] transition-all duration-150 border-b border-white/[0.03] cursor-pointer group"
                                            onClick={() => handleEdit(student)}
                                        >
                                            <td className="px-6 py-4">
                                                <div className="flex items-center gap-3">
                                                    <Avatar className="h-8 w-8 border border-white/10 shadow-sm group-hover:border-emerald-500/30 transition-colors">
                                                        <AvatarFallback className="bg-emerald-500/10 text-emerald-500 text-[10px] font-bold">
                                                            {student.name?.charAt(0) || "S"}
                                                        </AvatarFallback>
                                                    </Avatar>
                                                    <span className="font-semibold text-slate-200 text-sm group-hover:text-white transition-colors">{student.name}</span>
                                                </div>
                                            </td>
                                            <td className="px-6 py-4">
                                                <span className="text-xs text-slate-500 font-mono">{student.phone}</span>
                                            </td>
                                            <td className="px-6 py-4">
                                                <Badge className="bg-emerald-500/10 text-emerald-400 border-emerald-500/20 capitalize">
                                                    {student.lead_status || "Enrolled"}
                                                </Badge>
                                            </td>
                                            <td className="px-6 py-4">
                                                <span className="text-xs text-slate-500">
                                                    {new Date(student.created_at).toLocaleDateString('en-IN', { month: 'short', day: 'numeric', year: 'numeric' })}
                                                </span>
                                            </td>
                                            <td className="px-6 py-4 text-right">
                                                <div className="flex justify-end gap-2">
                                                    <Button size="icon" variant="ghost" className="h-8 w-8 text-slate-500 hover:text-white hover:bg-white/10">
                                                        <ArrowUpRight className="h-4 w-4" />
                                                    </Button>
                                                    <Button
                                                        size="icon"
                                                        variant="ghost"
                                                        className="h-8 w-8 text-slate-500 hover:text-red-500 hover:bg-red-500/10"
                                                        onClick={(e) => handleDelete(student.id, e)}
                                                    >
                                                        <Trash2 className="h-4 w-4" />
                                                    </Button>
                                                </div>
                                            </td>
                                        </tr>
                                    ))
                                )}
                            </tbody>
                        </table>
                    </div>
                </div>
            </div>

            {/* MANAGEMENT DIALOG */}
            <Dialog open={isDialogOpen} onOpenChange={setIsDialogOpen}>
                <DialogContent className="bg-[#0B0F19] border-white/10 text-white max-w-md p-0 overflow-hidden rounded-2xl">
                    <DialogHeader className="p-6 border-b border-white/5 bg-white/[0.02]">
                        <DialogTitle className="flex items-center gap-2 text-xl">
                            <User className="h-5 w-5 text-emerald-500" />
                            Manage Student
                        </DialogTitle>
                    </DialogHeader>

                    <div className="p-6 space-y-4">
                        <div className="space-y-1.5">
                            <label className="text-[10px] uppercase font-bold text-slate-500 tracking-wider">Full Name</label>
                            <Input
                                value={formData.name}
                                onChange={(e) => setFormData({ ...formData, name: e.target.value })}
                                className="bg-white/5 border-white/10 focus:border-emerald-500/50"
                            />
                        </div>
                        <div className="space-y-1.5">
                            <label className="text-[10px] uppercase font-bold text-slate-500 tracking-wider">Phone Number</label>
                            <Input
                                value={formData.phone}
                                onChange={(e) => setFormData({ ...formData, phone: e.target.value })}
                                className="bg-white/5 border-white/10 focus:border-emerald-500/50 font-mono"
                            />
                        </div>
                        <div className="space-y-1.5">
                            <label className="text-[10px] uppercase font-bold text-slate-500 tracking-wider">Status</label>
                            <Input
                                value={formData.lead_status}
                                onChange={(e) => setFormData({ ...formData, lead_status: e.target.value })}
                                className="bg-white/5 border-white/10 focus:border-emerald-500/50"
                            />
                        </div>
                    </div>

                    <div className="p-6 border-t border-white/5 bg-white/[0.02] flex gap-3">
                        <Button
                            className="flex-1 bg-emerald-600 hover:bg-emerald-700 text-white font-bold"
                            onClick={handleSave}
                            disabled={isSaving}
                        >
                            {isSaving ? "Saving..." : "Save Changes"}
                        </Button>
                        <Button
                            variant="destructive"
                            className="bg-red-500/10 hover:bg-red-500 text-red-500 hover:text-white border border-red-500/20"
                            onClick={() => handleDelete(selectedStudent?.id)}
                        >
                            <Trash2 className="h-4 w-4" />
                        </Button>
                    </div>
                </DialogContent>
            </Dialog>
        </div>
    )
}
